<?php
set_time_limit(0); // 取消脚本运行时间的超时上限
$content = "";
$filelength = 10*1024*1024; // 10M
$filename = "data/10M.log";
$content = "";
$contentSize = strlen($content);
$contentTimes = (int)($filelength/$contentSize); 
$starttime = time();

for($i1 = 0; $i1 <$contentTimes; $i1++){
	file_put_contents($filename, $content, LOCK_EX|FILE_APPEND);
}
$endtime = time();
echo "Start @".$starttime."  and end @".$endtime." and spend ".($endtime - $starttime)." seconds";
?>