<?php
	set_time_limit(0); // 取消脚本运行时间的超时上限
	$content = "";
	$filelength = 10000000;
	while(strlen($content) < $filelength){
		$content = $content."";
	}
	$filename = "data/10M.log";
	$starttime = time();
	file_put_contents($filename, $content);
	$endtime = time();
	echo "Start @".$starttime."  and end @".$endtime." and spend ".($endtime - $starttime)." seconds";	
?>